package com.ktdsuniversity.edu.enums;

public class Main {

	public static void main(String[] args) {
		
		Person kim = new Person(30000);
		
		Store emart = new Store(3000, 50);
		
		// kim이 emart에서 상품 3개를 카드로 결제했다.
		kim.buy(emart, 3, Payments.CARD);
		emart.printAll();
		kim.buy(emart, 2, Payments.BANKING);
		emart.printAll();
		kim.buy(emart, 1, Payments.POINT);
		emart.printAll();
		
		// kim이 emart에서 상품 5개를 환불했고 환불금은 계좌 이체로 받는다.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
		kim.refund(emart, 5, Payments.BANKING);
		emart.printAll();
	}
}
